import React from 'react';
import { Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Navbar.css';

export default class Navbar extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            navbarOpen: false // Estado para el menú colapsable
        };
    }

    // Función para alternar el estado del menú colapsable
    toggleNavbar = () => {
        this.setState({ navbarOpen: !this.state.navbarOpen });
    }

    render() {
        // Determina la clase CSS para el menú colapsable
        const navbarClasses = this.state.navbarOpen ? 'collapse navbar-collapse show' : 'collapse navbar-collapse';

        return (
            <nav className="navbar navbar-expand-md navbar-light bg-grey">
                <div className="container">
                    <Link to="/" className="navbar-brand">
                        <img src="./img/SSU.png" alt="Logo SSU" style={{ maxWidth: '50px', height: 'auto' }} />
                    </Link>
                    <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded={this.state.navbarOpen} aria-label="Toggle navigation" onClick={this.toggleNavbar}>
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    <div className={navbarClasses} id="navbarSupportedContent">
                        <ul className="navbar-nav ml-auto">
                            <li className="nav-item">
                                <Link to="/" className="nav-link">
                                    <i className="bi bi-house"></i> Inicio
                                </Link>
                            </li>
                            <li className="nav-item">
                                <a href="/nosotros" className="nav-link scroll-link">
                                    <i className="bi bi-person"></i> Sobre nosotros
                                </a>
                            </li>
                            <li className="nav-item">
                                <Link to="/jugadores" className="nav-link">
                                    <i className="bi bi-person-badge-fill"></i> Nuestros jugadores
                                </Link>
                            </li>
                            <li className="nav-item">
                                <Link to="/contacto" className="nav-link">
                                    <i className="bi bi-images"></i> Contacto
                                </Link>
                            </li>
                            <li className="nav-item">
                                <Link to="/otros" className="nav-link">
                                    <i className="bi bi-controller"></i> ¿Quieres conocer otros juegos?
                                </Link>
                            </li>
                        </ul>
                    </div>
                </div>
            </nav>
        );
    }
}
