import React from "react";
import ReactDOM from "react-dom";

class Proyecto extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      mostrar: false,
    };

    this.cambiarMostrar = this.cambiarMostrar.bind(this);
  }

  cambiarMostrar() {
    var mostrarActual = this.state.mostrar;
    this.setState({
      mostrar: !mostrarActual,
    });
  }

  render() {
    var clase = "";
    var texto = "";
    if (this.state.mostrar) {
      clase = " d-block";
      texto = "Mostrar menos";
    } else {
      clase = " d-none";
      texto = "Mostrar mas";
    }
    return (
      <React.Fragment>
        <img
          src={"img/" + this.props.imagen}
          alt="imagen"
          width="100px"
          height="120px"
          className="mr-3 d-none d-sm-block"
        />
        <div className={"d-flex justify-content-between flex-column" + clase}>
          <h4>{this.props.titulo}</h4>

          <p className={clase}>{this.props.descripcion}</p>
          <div>
            <button
              className="btn btn-primary"
              onClick={this.cambiarMostrar}
            >
              {texto}
            </button>
            <a
              href={this.props.link}
              target="_blank"
              className={"btn btn-" + this.props.boton}
            ></a>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

class App extends React.Component {
  render() {
    return (
      <div>
        <Proyecto
          titulo="¿Quienes somos?"
          descripcion="Somos un equipo de eSports de Ushuaia, Tierra del Fuego. Todos nuestros miembros son Fueguinos, y seguimos en búsqueda para agrandar nuestras filas en las distintas disciplinas."
          imagen="a.png"
        />
        <Proyecto
          titulo="¿Cuál es nuestro objetivo?"
          descripcion="Nuestro objetivo es hacer crecer los eSports en Tierra del Fuego y competir al nivel mas alto en cada juego, y también, hacer crecer la escena de la creación de contenido con nuestros creadores."
          imagen="c.png"
        />
        <Proyecto
          titulo="¿Qué disciplinas tienen?"
          descripcion="Counter-Strike: Global Offensive, Valorant, League Of Legends, Fortnite, Rocket League, FIFA, Rainbow Six Siege y algunos más. ¡En la sección Contacto podes postularte!"
          imagen="b.png"
        />
      </div>
    );
  }
}

ReactDOM.render(<App />, document.getElementById("root"));
