import React, { Component } from 'react';
import './App.css';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';

import Menu from './componentes/Menu';

class App extends Component {
  state = {
    currentRoute: '', // Ruta actual
  };

  // Función para actualizar la ruta actual en el estado
  handleRouteChange = (route) => {
    this.setState({ currentRoute: route });
  };

  render() {
    const { currentRoute } = this.state;

    // Importaciones condicionales de hojas de estilo según la ruta
    const currentStylesheet = require(`./${currentRoute}.css`).default; // Cambia la ruta según tu estructura de archivos CSS

    return (
      <Router>
        <div>
          <Menu />
          <div className="container pt-5 mt-5">
            <Routes>
              <Route
                path="/"
                element={<InicioPagina onRouteChange={this.handleRouteChange} />}
              />
              <Route
                path="/contacto"
                element={<ContactoPagina onRouteChange={this.handleRouteChange} />}
              />
              <Route
                path="/jugadores"
                element={<JugadoresPagina onRouteChange={this.handleRouteChange} />}
              />
              <Route
                path="/nosotros"
                element={<SobreNosotros onRouteChange={this.handleRouteChange} />}
              />
              <Route
                path="/otros"
                element={<Otros onRouteChange={this.handleRouteChange} />}
              />
            </Routes>
          </div>
          {/* Carga dinámica de hojas de estilo según la ruta */}
          <style>{currentStylesheet}</style>
        </div>
      </Router>
    );
  }
}

export default App;
