import React from "react";

class ContactoPagina extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      nombre: "",
      mensaje: "",
    };

    this.submitted = this.submitted.bind(this);
    this.changed = this.changed.bind(this);
  }

  submitted(event) {
    alert("Se envió el formulario de " + this.state.nombre);
    event.preventDefault();
  }

  changed(event) {
    this.setState({
      [event.target.name]: event.target.value,
    });
  }

  render() {
    const containerStyle = {
      background: "#ccc", // Color gris de fondo
      color: "white", // Texto en blanco
      padding: "20px", // Espaciado interior
    };

    return (
      <div style={containerStyle}>
        <div className="container-fluid">
          <div className="row">
            <div className="col-12 col-sm-6">
              <h2>Contacto</h2>
            </div>
          </div>
          <div className="row">
            <div className="col-6">
              <form onSubmit={this.submitted}>
                <div className="form-group my-2">
                  <label>Nombre</label>
                  <input
                    type="text"
                    className="form-control"
                    name="nombre"
                    value={this.state.nombre}
                    onChange={this.changed}
                  />
                </div>
                <div className="form-group my-2">
                  <label>Mensaje</label>
                  <textarea
                    className="form-control"
                    name="mensaje"
                    value={this.state.mensaje}
                    onChange={this.changed}
                  ></textarea>
                </div>
                <button type="submit" className="btn btn-danger mt-2">
                  Enviar
                </button>
              </form>
            </div>
            <div className="col-12 col-sm-6">
              <p className="font-weight-bold">Datos de contacto</p>
              <p>
                <i className="bi bi-envelope-at"></i> ssu_ush@hotmail.com
              </p>
              <p>
                <i className="bi bi-instagram"></i>
                <a href="https://www.instagram.com/ssu_ush" target="_blank">
                  Instagram
                </a>
              </p>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default ContactoPagina;
